import { Injectable } from '@angular/core';
import { Recipe } from './recipe';
import { Ingredient } from '../ingredient';

@Injectable()
export class RecipeService {

  private recipes: Recipe[] = [
    new Recipe('Aloo Fry', 'Pour oil, fry potates in the oil. Add salt and red pepper powder', 
              'data:image/jpeg;base64,',
              [
               new Ingredient('french fries', 2),
               new Ingredient('chow mein', 1)
              ]),
    new Recipe('Tomato dal', 'Cook dal and tomato at once, add salt and Add spices', 
      'data:image/jpeg;base64,',
      [])
  ];

  constructor() { }  

  public setRecipes(newRecipes: Recipe[]){
    this.recipes = newRecipes;
  }

  public getRecipes(){
  	return this.recipes;
  }

  public deleteRecipe(recipe: Recipe){
    this.recipes.splice(this.recipes.indexOf(recipe), 1);
  }

  public getRecipe(recipeIndex: number){
    return this.recipes[recipeIndex];
  }

  public addRecipe(recipe: Recipe){
    this.recipes.push(recipe);
  }

  public editRecipe(oldRecipe: Recipe, newRecipe: Recipe){
    this.recipes[this.recipes.indexOf(oldRecipe)] = newRecipe;
  }

}
